    useEffect(() => {
    
        const fetchData = async () => {
        await fetch(Util.URL_REST+"api/product/getPdt/"+pdtId, {
            method: "GET",
            headers: Util.headersList
            }).then((res) => res.json())
            .then((json) => {
                setValue({
                    product: json,
                    description: json.description,
                });
        })
        };
        fetchData();
      }, []);
