/* eslint-disable jsx-a11y/img-redundant-alt */
import React from "react";
import { Link } from 'react-router-dom'
import Util from "./Util"

class ListProduct extends React.Component {
    
    // Constructor
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            DataisLoaded: false,
            'pdtName': '*',
            pdtKinds:[],
            'kind':'*'
        };
    }
    openImg = (src) => {
        window.location.href = src;
    }
    searchProduct = () => {
        var pdtName = this.state.pdtName;
        var kind = this.state.kind;
        if(pdtName === ""){
            pdtName = "*";
        }
        if(kind === ""){
            kind = "*";
        }
        
        fetch(Util.URL_REST+"api/product/getProduct/" + pdtName +"/" + kind, {
            method: "GET",
            headers: Util.headersList
        }).then((res) => res.json())
            .then((json) => {
                this.setState({
                    products: json,
                    DataisLoaded: true
                });
            })

    }
    setPram = (event) => {
        this.setState({ [event.target.name]: event.target.value.trim() });
    }
    componentDidMount() {
        fetch(Util.URL_REST+"api/product/getProduct/*/*", {
            method: "GET",
            headers: Util.headersList
        }).then((res) => res.json())
            .then((json) => {
                this.setState({
                    products: json,
                    DataisLoaded: true
                });
            })
        fetch(Util.URL_REST+"api/product/getPdtKind", {
                method: "GET",
                headers: Util.headersList
            }).then((res) => res.json())
                .then((json) => {
                    this.setState({
                        pdtKinds: json
                    });
                })

    }
    render() {
        const { DataisLoaded, products, pdtKinds } = this.state;
        if (!DataisLoaded) return <div>
            <h6 className="text-title-cl"> Plesea login.... </h6> </div>;
        else
            return (
       <div>
     <meta charSet="utf-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>Thegioididong.com - Điện thoại, Laptop, Phụ kiện, Đồng hồ chính hãng</title>
      <meta name="keywords" content="Thế giới di động, Thegioididong, điện thoại di động, dtdd, smartphone, tablet, máy tính bảng, laptop, máy tính xách tay, phụ kiện, smartwatch, đồng hồ, tin công nghệ" />
      <meta name="description" content="Hệ thống bán lẻ điện thoại di động, smartphone, máy tính bảng, tablet, laptop, phụ kiện, smartwatch, đồng hồ chính hãng mới nhất, giá tốt, dịch vụ khách hàng được yêu thích nhất VN" />
      <meta property="og:title" content="Thegioididong.com - Điện thoại, Laptop, Phụ kiện, Đồng hồ chính hãng" />
      <meta property="og:description" content="Hệ thống bán lẻ điện thoại di động, smartphone, máy tính bảng, tablet, laptop, phụ kiện, smartwatch, đồng hồ chính hãng mới nhất, giá tốt, dịch vụ khách hàng được yêu thích nhất VN" />
      <meta content="INDEX,FOLLOW" name="robots" />
      <meta name="viewport" content="width=device-width" />
      <meta name="copyright" content="Công ty Cổ phần Thế Giới Di Động" />
      <meta name="author" content="Công ty Cổ phần Thế Giới Di Động" />
      <meta httpEquiv="audience" content="General" />
      <meta name="resource-type" content="Document" />
      <meta name="distribution" content="Global" />
      <meta name="revisit-after" content="1 days" />
      <meta name="GENERATOR" content="Công ty Cổ phần Thế Giới Di Động" />
      <meta httpEquiv="X-UA-Compatible" content="IE=edge,chrome=1" />
      <link href="/favicon_TGDD.ico" rel="shortcut icon" type="image/x-icon" />
      <link href="/favicon_TGDD.ico" rel="apple-touch-icon" />
      <link href="/favicon_TGDD.ico" rel="apple-touch-icon-precomposed" />
      <meta property="og:site_name" content="Thegioididong.com" />
      <meta property="og:type" content="website" />
      <meta property="og:locale" content="vi_VN" />
      <meta property="fb:pages" content={214993791879039} />
      <meta httpEquiv="x-dns-prefetch-control" content="on" />
         <div className="block-product relative">
             <div className="preloader">
               <div className="loaderweb" />
         </div>
            <div className="block-product__content"></div>
            <ul className="listproduct">
                            {
                                products.map(
                                    product =>
                                    <li data-id={250261} data-pos={1} className="item ">
                                    <a href="/dtdd/iphone-13-pro-max-256gb" className=" main-contain" data-s="OnlineSavingCMS" data-site={1} data-pro={3} data-cache="True" data-name="Điện thoại iPhone 13 Pro Max 256GB" data-id={250261} data-price={33190000.0} data-brand="iPhone (Apple)" data-cate="Điện thoại" data-box="BoxHomeV2">
                                      <div className="item-label">
                                      </div>
                                      <div className="item-img">
                                        <img data-src="https://cdn.tgdd.vn/Products/Images/42/250261/iphone-13-pro-max-gold-1-600x600.jpg" className="lazyload" alt="iPhone 13 Pro Max 256GB" width={207} height={207} />
                                      </div>
                                      <p className="result-label temp4"><img width={20} height={20} className="lazyload" alt="VNPAY GIẢM 500k" data-src="https://cdn.tgdd.vn/2020/10/content/icon6-50x50.png" /><span>VNPAY GIẢM 500k</span></p>        <h3>iPhone 13 Pro Max 256GB</h3>
                                      <p className="item-txt-online">Online giá rẻ</p>
                                      <strong className="price">
                                        33.190.000₫
                                        <small>-10%</small>
                                      </strong>
                                      <p className="vote-txt">
                                        <b>4.5</b><i className="iconnewglobal-vote" />(185)
                                      </p>
                                    </a>
                                  </li>
                            )
                         }
            </ul>    
           </div>      
           </div>   
            );
    }
}

export default ListProduct;
