import React from "react";
import Util from "./Util";
class TransCoin extends React.Component {

    // Constructor
    constructor(props) {
        super(props);
        this.state = {
            coins: [],
            DataisLoaded: false,
            searchCoin: '',
        };
    }

    UpdatePrice = (coinId) => {
        fetch(Util.URL_REST + "api/coin/getMaketCap/" + coinId).then((res) => res.json())
            .then((json) => {
                console.log(json);
                this.componentDidMount();
            })

    }

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        //  alert(this.state.activePage);
        this.setState({ activePage: pageNumber });

        fetch(Util.URL_REST + "api/coin/getAllCoin/" + pageNumber, {
            method: "GET",
            headers: Util.headersList
        }).then((res) => res.json())
            .then((json) => {
                console.log(json);
                this.setState({
                    coins: json,
                    DataisLoaded: true
                });
            })

    }

    searchCoin = () => {

        var coinId = this.state.searchCoin;
        fetch(Util.URL_REST + "api/coin/getMaketCap/" + coinId)
            .then((res) => res.json())
            .then((json) => {
                var arr = new Array(json);
                if (json.statusCode === 2) {
                    alert(json.message)
                    return false;
                }
                this.componentDidMount(arr);
            })

    }
    setPram = (event) => {
        this.setState({ [event.target.name]: event.target.value.trim() });
    }
    componentDidMount(item) {
        if (item == null) {
            fetch(Util.URL_REST + "api/coin/getAllCoin/" + 1, {
                method: "GET",
                headers: Util.headersList
            }).then((res) => res.json())
                .then((json) => {
                    console.log(json);
                    this.setState({
                        coins: json,
                        DataisLoaded: true
                    });
                })
        } else {
            this.setState({
                coins: item,
                DataisLoaded: true
            })
        }

    }


    render() {
            return (
            <div>
                <meta charSet="UTF-8" />
                <title>Currency converter demo</title>
                <link href="../dist/currency-converter.css" rel="stylesheet" />
                <link href="style.css" rel="stylesheet" />
                <link href="https://prismjs.com/themes/prism-coy.css" rel="stylesheet" />
                <div className="meta">
                  <h1>Currency converter</h1>
                </div>
                <section>
                  <div className="section-container">
                    <div className="meta-area">
                      <h2 className="section-title">
                        Example: 1
                        <code className="blue">Default options</code>
                      </h2>
                    </div>
                    <div className="ex-section ex1">
                      <div className="code-area">
                        <pre className="code-container"><code className="language-javascript">currencyConverter("#example-1");</code></pre>
                      </div>
                      <div className="ex-area">
                        <div className="ex-container">
                          <div id="example-1" />
                        </div>
                      </div>
                    </div>
                  </div>
                </section>
              </div>
            );
    }
}

export default TransCoin;
