import './App.css';
import React, {Component} from 'react';
import Header from './components/Header';
import Product from './components/Product';

class App extends Component{
  render(){

    return(
      <div>
        <Header></Header>
        <div className="row" onClick={() => showButon.setShow((s) => !s)} >
          <div className="col-xs-5 col-sm-5 col-md-5 col-lg-5" style={{ display: showButon.show ? "block" : "none" }}>
            <Product></Product>
          </div>
        </div>

      </div>

    )
  }
}

function showButon(){
  const [show, setShow] = Component(true);
}

export default App;
