import React from "react";
import {Link} from 'react-router-dom'
export default class Header extends React.Component{

    constructor(props){
        super(props)
        var userItem = localStorage.getItem('userItem');
        if(userItem == null){
            this.state = {
                text:'App isLogout',
            } 
        }else{
            this.state = {
                text: 'Welcome ' + userItem + ' !!' ,
            } 
        }
        this.logout = this.logout.bind(this) ;
      
    }
    setParams  = (event) =>{
        this.setState({[event.target.name] : event.target.value})

    }
    logout(){
       if(localStorage.getItem('token') != null){
        localStorage.removeItem("token");
        localStorage.removeItem("userItem");
        this.setState({text:'App isLogout'});
        alert("logout suscess");
        window.location.reload(false);
       }
    }

    render(){
 return (
        <nav className="navbar navbar-inverse">
            <ul className="nav navbar-nav">
                <li >
                    <Link to="/" >Home</Link>
                </li >
                <li >
                    <Link to="/coin">Coin</Link>
                </li>
                <li >
                    <Link to="/product">Product</Link>
                </li>
               
                <li>
                    <Link to="/balance">Balance</Link>
                </li>
                <li>
                    <Link to="/signUp">Sign Up</Link>
                </li>
                <li>
                     <button onClick={this.logout}>Logout</button>
                     <button> <Link to="/login">Login</Link></button>
                </li>
                <input value = {this.state.text}/>
            </ul>
    <div className="d-none d-lg-flex ml-auto flex-column flex-lg-row align-items-center">
        <ul className="navbar-nav  ">
          <li className="nav-item">
            <a className="nav-link" href="fruit.html">
              <img src="images/login.png" alt="" />
              <span>Login</span></a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="service.html">
              <img src="images/signup.png" alt="" />
              <span>Sign Up</span>
            </a>
          </li>
        </ul>
        <form className="form-inline my-2 mb-3 mb-lg-0 mr-5">
          <button className="btn  my-2 my-sm-0 nav_search-btn" type="submit" />
        </form>
      </div>   
        </nav>
        )
    }
}
