import React from "react";
export default class Login extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            "userName":"",
            "password":"",
        }
        this.setParams = this.setParams.bind(this) ;

        
    }
    setParams  = (event) =>{
        this.setState({[event.target.name] : event.target.value.trim()})
    }
    login = () =>{
        
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        var raw = JSON.stringify({
        "userName": this.state.userName,
        "password": this.state.password
        });

        var requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: raw,
        redirect: 'follow'
        };

        fetch("http://localhost:8089/api/authenticate", requestOptions)
        .then(response =>{
            console.log(response)
            if(response.ok){
                return response.json()
            }
            throw Error(response.status)
        })     
        .then(result => {console.log(result)
            localStorage.setItem("token" , result.jwt);
            localStorage.setItem("userItem" , result.userName);
            alert("suscess");
            window.location.href = "http://localhost:3000/";
        })
        .catch(error => {console.log('error', error)
        alert("userName or Password are wrong ")
    });
    window.location.reload(false);
    }
    render(){
        return (
        
          <div className="login-box" >
              <h2>Login</h2>
                <div className="user-box">
                  <input type="text"  name = "userName" onChange={this.setParams} value = {this.state.userName}/>
                  <label>Username</label>
                </div>
                <div className="user-box">
                  <input type="password" name = "password" onChange={this.setParams} value = {this.state.password} />
                  <label>Password</label>
                </div>
               <button  onClick={this.login}>Login</button>
            </div>
            
          );      
    }
}
