/* eslint-disable jsx-a11y/anchor-has-content */
import React from "react";
import { Link } from 'react-router-dom'
export default class HeaderProduct extends React.Component {
    constructor(props){
        super(props)
        var userItem = localStorage.getItem('userItem');
        
        if(userItem == null){
            this.state = {
                text:'',
                image:'',
                showHide:false
            } 
        }else{
            this.state = {
                text:'',
                image:'',
                showHide:false
            } 
        }
        
        this.logout = this.logout.bind(this) ;
      
    }
    render() {
        return (
            <div>
                <div class="navbar" style={{ background: "black" }}>
                    <a class="navbar-brand" href="/sa"></a>
                    <ul class="nav navbar-nav">
                        <li onClick={() => this.setState({active:1})} className={this.state.active === 1 ? 'active-btn' : null}>
                            <Link to={'/listProduct'}>List Product </Link>
                        </li>
                        <li onClick={() => this.setState({active:2})} className={this.state.active === 2 ? 'active-btn' : null}>
                            <Link to={'/addProduct'}>Add Product </Link>
                        </li>
                    </ul>
                </div>
            </div>
        );
    }
}
