import React from "react";
const token = localStorage.getItem('token');
const AuthStr = 'Bearer '+token;
class User extends React.Component {

	// Constructor
	constructor(props) {
		super(props);
		this.state = {
			balances: [],
			DataisLoaded: false
		};
	}

   

	// ComponentDidMount is used to
	// execute the code
	componentDidMount() {
        let headersList = {
            "User-Agent": "Thunder Client (https://www.thunderclient.com)",
            "Accept-Language": "application/json",
            "Authorization":AuthStr
           } 
        fetch("http://localhost:8089/api/account/balance", { 
             method: "GET",
             headers: headersList
         }).then((res) => res.json())
           .then((json) => {
               console.log(json);
               this.setState({
                   balances: json,
                   DataisLoaded: true
               });
           })
   }
		
			
	render() {
		const { DataisLoaded, balances } = this.state;
		if (!DataisLoaded) return <div>
			<h1> Pleses wait some time.... </h1> </div> ;
		else 
        return (
        <div> 
            <h1>Balance your</h1>
            <table className="table table-hover">
                <thead>
                    <tr>
                        <th>id</th>
                        <th>accountId</th>
                        <th>coinId</th>
                        <th>contract</th>
                        <th>quantityCoin</th>
                        <th>timeUpdate</th>
                    </tr>
                </thead>
                <tbody>
                    {
                       balances.map(
                       coin =>
                       <tr key = {coin.id}>
                           <td>{coin.id}</td>
                           <td id ="accountid">{coin.accountId}</td>
                           <td>{coin.coinId}</td>
                           <td>{coin.contract}</td>
                           <td>{coin.quantityCoin}</td>
                           <td>{coin.timeUpdate}</td>
                       </tr>
                           )
                    }
                </tbody>
            </table>
       </div>
	);
}
}

export default User;
