/* eslint-disable jsx-a11y/img-redundant-alt */
import React from "react";
import { Link } from 'react-router-dom'
import Util from "./Util"
import Pagination from "react-js-pagination";
class ListProduct extends React.Component {
    
    // Constructor
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            DataisLoaded: false,
            'pdtName': '*',
            pdtKinds:[],
            'kind':'*'
        };
    }
    openImg = (src) => {
        window.location.href = src;
    }
    searchProduct = () => {
        var pdtName = this.state.pdtName;
        var kind = this.state.kind;
        if(pdtName === ""){
            pdtName = "*";
        }
        if(kind === ""){
            kind = "*";
        }
        
        fetch(Util.URL_REST+"api/product/getProduct/" + pdtName +"/" + kind, {
            method: "GET",
            headers: Util.headersList
        }).then((res) => res.json())
            .then((json) => {
                this.setState({
                    products: json,
                    DataisLoaded: true
                });
            })

    }

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        //  alert(this.state.activePage);
        this.setState({ activePage: pageNumber });

        fetch(Util.URL_REST + "api/coin/getAllCoin/" + pageNumber, {
            method: "GET",
            headers: Util.headersList
        }).then((res) => res.json())
            .then((json) => {
                console.log(json);
                this.setState({
                    coins: json,
                    DataisLoaded: true
                });
            })

    }
    setPram = (event) => {
        this.setState({ [event.target.name]: event.target.value.trim() });
    }
    componentDidMount() {
        fetch(Util.URL_REST+"api/product/getProduct/*/*", {
            method: "GET",
            headers: Util.headersList
        }).then((res) => res.json())
            .then((json) => {
                this.setState({
                    products: json,
                    DataisLoaded: true
                });
            })
        fetch(Util.URL_REST+"api/product/getPdtKind", {
                method: "GET",
                headers: Util.headersList
            }).then((res) => res.json())
                .then((json) => {
                    this.setState({
                        pdtKinds: json
                    });
                })

    }
    render() {
        const { DataisLoaded, products, pdtKinds } = this.state;
        if (!DataisLoaded) return <div>
            <h6 className="text-title-cl"> Plesea login.... </h6> </div>;
        else
            return (
       <div>
    <meta charSet="utf-8" />
    
      
        
     

       <div action="/tim-kiem" class="header__search"><input   onChange={this.setPram} type="text" class="input-search" placeholder="search product..." name="pdtName" autocomplete="off" maxlength="100"/><button type="submit" ><i class="glyphicon glyphicon-search" onClick={this.searchProduct}></i></button><div id="search-result">
      
       </div>
       <select  id="input" class="form-control" required="required" onChange={this.setPram} name = "kind">
       <option value= "*">All Product kind</option>
                            {
                                pdtKinds.map(
                                    product =>
                                     <option value={product.pdtKind}>{product.pdtKind}</option>
                                )
                            }
       </select>
       </div>                    
         <div className="block-product relative">
             <div className="preloader">
               <div className="loaderweb" />
         </div>
            <div className="block-product__content"></div>
            <ul className="listproduct">
                            {
                                products.map(
                                    product =>
                                    <li className="item ">
                                    <a href={'/productDetail/'+product.pdtId } className=" main-contain" data-s="OnlineSavingCMS" data-site={1} data-pro={3} data-cache="True" data-name="Điện thoại iPhone 13 Pro Max 256GB" data-id={250261} data-price={33190000.0} data-brand="iPhone (Apple)" data-cate="Điện thoại" data-box="BoxHomeV2">
                                      <div className="item-label">
                                      </div>
                                      <div className="item-img">
                                        <img src= {Util.URL_REST+ product.image} alt="image" width={207} height={207} />
                                      </div>
                                      <p className="result-label temp4"><img width={20} height={20} className="lazyload" alt="VNPAY GIẢM 500k" data-src="https://cdn.tgdd.vn/2020/10/content/icon6-50x50.png" /><span>Buy now</span></p>        <h3>{product.pdtName}</h3>
                                      <p className="item-txt-online">Online</p>
                                      <strong className="price">
                                         {Util.setComma(product.price)} {''} / {''}{product.kindCoin} 
                                        {/* <small>-10%</small> */}
                                      </strong>
                                      <p className="vote-txt">
                                        {/* <b>4.5</b><i className="iconnewglobal-vote" />(185) */}
                                      </p>
                                    </a>
                                    {
                                                        Util.userDetail.role == "ADMIN"?
                                                        <Link to={'/addProduct/' + product.pdtId}  className="btn">
                                                            <span  aria-hidden="true"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-wrench-adjustable" viewBox="0 0 16 16">
                                                                <path d="M16 4.5a4.492 4.492 0 0 1-1.703 3.526L13 5l2.959-1.11c.027.2.041.403.041.61Z"/>
                                                                <path d="M11.5 9c.653 0 1.273-.139 1.833-.39L12 5.5 11 3l3.826-1.53A4.5 4.5 0 0 0 7.29 6.092l-6.116 5.096a2.583 2.583 0 1 0 3.638 3.638L9.908 8.71A4.49 4.49 0 0 0 11.5 9Zm-1.292-4.361-.596.893.809-.27a.25.25 0 0 1 .287.377l-.596.893.809-.27.158.475-1.5.5a.25.25 0 0 1-.287-.376l.596-.893-.809.27a.25.25 0 0 1-.287-.377l.596-.893-.809.27-.158-.475 1.5-.5a.25.25 0 0 1 .287.376ZM3 14a1 1 0 1 1 0-2 1 1 0 0 1 0 2Z"/>
                                                                </svg>
                                                            </span>
                                                        </Link>:null
                                                        }
                                  </li>
                            )
                         }
            </ul>    
           </div>
           <Pagination
                        activePage={this.state.activePage}
                        itemsCountPerPage={10}
                        totalItemsCount={4500}
                        onChange={this.handlePageChange.bind(this)}
                    />      
           </div>   
            );
    }
}

export default ListProduct;
