import React from "react";
import {Link} from 'react-router-dom'
export default class Header extends React.Component{

    constructor(props){
        super(props)
        var userItem = localStorage.getItem('userItem');
        if(userItem == null){
            this.state = {
                text:'App isLogout',
            } 
        }else{
            this.state = {
                text: 'Welcome ' + userItem + ' !!' ,
            } 
        }
        this.logout = this.logout.bind(this) ;
      
    }
    setParams  = (event) =>{
        this.setState({[event.target.name] : event.target.value})

    }
    logout(){
       if(localStorage.getItem('token') != null){
        localStorage.removeItem("token");
        localStorage.removeItem("userItem");
        this.setState({text:'App isLogout'});
        alert("logout suscess");
        window.location.reload(false);
       }
    }

    render(){
        return (

            <header className="header_section">
              <div className="container-fluid">
                <nav className="navbar navbar-expand-lg custom_nav-container">
                  <a className="navbar-brand" href="index.html">
                    <span>
                      Intot
                    </span>
                  </a>
                  <div className="navbar-collapse" id>
                    <div className="d-none d-lg-flex ml-auto flex-column flex-lg-row align-items-center">
                      <ul className="navbar-nav  ">
                        <li className="nav-item">
                          <a className="nav-link" href="fruit.html">
                            <img src="images/login.png" alt="" />
                            <span>Login</span></a>
                        </li>
                        <li className="nav-item">
                          <a className="nav-link" href="service.html">
                            <img src="images/signup.png" alt="" />
                            <span>Sign Up</span>
                          </a>
                        </li>
                      </ul>
                      <form className="form-inline my-2 mb-3 mb-lg-0 mr-5">
                        <button className="btn  my-2 my-sm-0 nav_search-btn" type="submit" />
                      </form>
                    </div>
                    <div className="custom_menu-btn">
                      <button onclick="openNav()">
                        <span className="s-1">
                        </span>
                        <span className="s-2">
                        </span>
                        <span className="s-3">
                        </span>
                      </button>
                    </div>
                    <div id="myNav" className="overlay">
                      <div className="overlay-content">
                        <a href="index.html">HOME</a>
                        <a href="team.html">TEAM</a>
                        <a href="portfolio.html">PORTFOLIO</a>
                        <a href="contact.html">CONTACT US</a>
                      </div>
                    </div>
                  </div>
                </nav>
              </div>
            </header>
          );
    }
}
