import React from "react";
export default class Login extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            "username":"",
            "password":"",
        }
       // this.setParams = this.setParams.bind(this) ;
        
    }
    setParams  = (event) =>{
        this.setState({[event.target.name] : event.target.value.trim()})

    }
    login = () =>{
        var raw = JSON.stringify({
        "userName": this.state.username,
        "password": this.state.password
        });

        var requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: raw,
        redirect: 'follow'
        };

        fetch("http://localhost:8089/api/authenticate", requestOptions)
        .then(response =>{
            console.log
        })     
        .then(result => {console.log(result)
            localStorage.setItem("token" , result.jwt);
            localStorage.setItem("userItem" , result.userName);
            alert("suscess");
            window.location.href = "http://localhost:3000/";
        })
        .catch(error => {console.log('error', error)
        alert("userName or Password are wrong ")
    });
    window.location.reload(false);
    }
    render(){
        return (
          <div className="login-box">
              <h2>Login</h2>
              <form>
                <div className="user-box">
                  <input type="text"  name = "username" onChange={this.setParams} autocomplete="off"/>
                  <label>Username</label>
                </div>
                <div className="user-box">
                  <input type="password" name = "password" onChange={this.setParams} autocomplete="off"/>
                  <label>Password</label>
                </div>
               <button  onClick={this.login}>Login</button>
              </form>
            </div>
            
          );      
    }
}
