import React from "react";
export default class Login extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            "username":"",
            "password":"",
        }
        
    }
    setParams  = (event) =>{
        this.setState({[event.target.name] : event.target.value})

    }
    login = () =>{
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        var raw = JSON.stringify({
        "userName": this.state.username,
        "password": this.state.password
        });

        var requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: raw,
        redirect: 'follow'
        };

        fetch("http://localhost:8089/api/authenticate", requestOptions)
        .then(response =>{
            console.log(response)
            if(response.ok){
                return response.json()
            }
            throw Error(response.status)
        })     
        .then(result => {console.log(result)
            localStorage.setItem("token" , result.jwt);
            localStorage.setItem("userItem" , result.userName);
            alert("suscess");
        })
        .catch(error => {console.log('error', error)
        alert("userName or Password are wrong ")
    });
    }
    render(){
        return <form>
            <div>
                <label>UserName:</label>
                <input type = "text" name = "username" onChange={this.setParams}/>
            </div>
            <div>
                <label>Password:</label>
                <input type = "password" name = "password" onChange={this.setParams}/>
            </div>
            <div>
                <button type = "button" onClick={this.login}>Login</button>
            </div>
        </form>
    }
}
