/* eslint-disable no-useless-constructor */
/* eslint-disable jsx-a11y/anchor-is-valid */

import React, {Component} from 'react';

class Product extends Component{
  constructor(props){
    super(props);
 //   this.onAddToCart = this.onAddToCart.bind(this);
  }
  onAddToCart(text){
       alert(text + ' Với Giá '+this.props.price + 'VND');
  }
  render(){
    return(
      <div>
        <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4 height-350">
          <div className="thumbnail">
            <img  alt={this.props.name} src ={this.props.image} className = "img-auto"/>
            <div className="caption">
              <h3>{this.props.name}</h3>
              <p>
                 {this.props.price}
              </p>
              <p>
                <a className="btn btn-primary" onClick={()=>{this.onAddToCart(this.props.name)}}>mua ngay</a>
              </p>
            </div>
          </div>
        </div>
      </div>
   
    )
  }
}
export default Product;
