    constructor(props){
        super(props)
        var userItem = localStorage.getItem('userItem');
        
        if(userItem == null){
            this.state = {
                text:'',
                image:'',
                showHide:false
            } 
        }else{
            this.state = {
                text:'',
                image:'',
                showHide:false
            } 
        }
        
        this.logout = this.logout.bind(this) ;
      
    }
    setParams  = (event) =>{
        this.setState({[event.target.name] : event.target.value})

    }
    logout(){
       if(localStorage.getItem('token') != null){
        localStorage.removeItem("token");
        localStorage.removeItem("userItem");
        localStorage.removeItem("photo");
        localStorage.removeItem("userDetail");
        window.location.href = Util.URL + "login";
       }
    }
    componentDidMount() {
        fetch(Util.URL_REST + "api/getUserDetail", {
            method: "GET",
            headers: Util.headersList
        }).then((res) => res.json())
            .then((json) => {
                if(Util.userDetail.role === 'ADMIN'){
                  this.setState({
                    showHide:true
                  })
                }
                this.setState({
                  image: Util.URL_REST + json.photo,
                  text:json.userName
                });
        })
          fetch(Util.URL_REST + "api/order/countTmt" ,{
            method: "GET",
            headers: Util.headersList
          }).then((res) => res.json())
            .then((json) => {
                console.log(json);
                this.setState({
                  count: json,
                });
            })
     }
