import React from 'react';
import Util from './Util';
    const getBalance =()=>{
        var js;
        fetch(Util.URL_REST + "api/account/balance/*/"+1, {
            method: "GET",
            headers: Util.headersList,
          })
            .then((res) => res.json())
            .then((json) => {
              console.log(json);
              js = json
            });
        return js;
    }
function PayMethod(props){
   
    if(props.name === "COIN"){
   
       return (
    <div>
        <h1>{props.name}</h1>
        
        <span class="input-group-btn">
            {
                getBalance.map(
                    balance =>{
                        <button type="button" class="btn btn-default">{balance.coinId}</button>
                    }
                )
            }
           
        </span>
        

        
    </div>)
        
       
    
    }
    else{
        return null;
    }
    
   
}
export default PayMethod;
