import React from "react";
export default class Login extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            "userName":"",
            "password":"",
        }
        this.setParams = this.setParams.bind(this);
        this.login = this.login.bind(this);
    }
    setParams(event){
        this.setState({[event.target.name] : event.target.value})
    }
    login(){
        
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        myHeaders.append("Cookie", "Cookie_1=value");
        
        var raw = JSON.stringify({
          "userName": this.state.userName,
          "password": this.state.password
        });
        var requestOptions = {
          method: 'POST',
          headers: myHeaders,
          body: raw,
          redirect: 'follow'
        };
        
        fetch("http://localhost:8089/api/authenticate", requestOptions)
          .then(response => response.text())
          .then(result => {console.log(result)
           
        })
          .catch(error => {console.log('error', error)
           });
    window.location.reload(false);
    }
    render(){
        return (
        
          <div className="login-box" >
              <h2>Login</h2>
                <div className="user-box">
                  <input type="text"  name = "userName" onChange={this.setParams} />
                  <label>Username</label>
                </div>
                <div className="user-box">
                  <input type="password" name = "password" onChange={this.setParams}/>
                  <label>Password</label>
                </div>
               <button  onClick={this.login}>Login</button>
            </div>
            
          );      
    }
}
